package com.example.myapplication.Pages

import androidx.compose.foundation.background
import androidx.compose.foundation.border
import androidx.compose.foundation.gestures.detectTapGestures
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.text.BasicTextField
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Brush
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.input.pointer.pointerInput
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import com.example.myapplication.navigation.Routes
import kotlinx.coroutines.*

@Composable
fun LoginPage(navController: NavController) {
    var email by remember { mutableStateOf("") }
    var password by remember { mutableStateOf("") }
    var buttonBackgroundColor by remember { mutableStateOf(Color(0xFF388E3C)) }
    var emailBorderColor by remember { mutableStateOf(Color.Gray) }
    var passwordBorderColor by remember { mutableStateOf(Color.Gray) }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(
                brush = Brush.verticalGradient(
                    colors = listOf(
                        Color(0xFFA5D6A7),
                        Color(0xFF81C784)
                    )
                )
            )
            .padding(horizontal = 32.dp),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.spacedBy(20.dp),
            modifier = Modifier.fillMaxWidth()
        ) {
            Text(
                text = "Bem-vindo ao Portal Acadêmico",
                style = MaterialTheme.typography.headlineMedium,
                color = Color(0xFF1B5E20),
                fontWeight = FontWeight.Bold
            )

            OutlinedTextField(
                value = email,
                onValueChange = { email = it },
                label = { Text("Email Institucional") },
                singleLine = true,
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 8.dp)
                    .border(1.dp, emailBorderColor)
                    .pointerInput(Unit) {
                        detectTapGestures(
                            onPress = {
                                emailBorderColor = Color.White
                                tryAwaitRelease()
                                emailBorderColor = Color.Gray
                            }
                        )
                    }
            )

            OutlinedTextField(
                value = password,
                onValueChange = { password = it },
                label = { Text("Senha") },
                singleLine = true,
                visualTransformation = PasswordVisualTransformation(),
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 8.dp)
                    .border(1.dp, passwordBorderColor)
                    .pointerInput(Unit) {
                        detectTapGestures(
                            onPress = {
                                passwordBorderColor = Color.White
                                tryAwaitRelease()
                                passwordBorderColor = Color.Gray
                            }
                        )
                    }
            )

            Button(
                onClick = {
                    if (email == "admin" && password == "1234") {
                        navController.navigate(Routes.ADMIN_SCREEN) {
                            popUpTo(Routes.LOGIN) { inclusive = true }
                        }
                    } else {
                        navController.navigate(Routes.HOME_SCREEN) {
                            popUpTo(Routes.LOGIN) { inclusive = true }
                        }
                    }
                },
                enabled = email.isNotBlank() && password.isNotBlank(),
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 8.dp)
                    .pointerInput(Unit) {
                        detectTapGestures(
                            onPress = {
                                buttonBackgroundColor = Color.White
                                tryAwaitRelease()
                                buttonBackgroundColor = Color(0xFF388E3C)
                            }
                        )
                    },
                colors = ButtonDefaults.buttonColors(containerColor = buttonBackgroundColor)
            ) {
                Text("Entrar", color = Color.White)
            }
        }
    }
}

